import React, { useState, useEffect } from "react";
import styles from "./Card.module.css";
import Wrap from "../Wrap/Wrap";
import settingsSign from "../images/other/settings.png";
import map from '../images/card/map.png';
import chip from '../images/card/chip.png';
import visa from '../images/card/visa.png';
import right from '../images/cardArrows/rightActive.png';
import left from '../images/cardArrows/leftActive.png';
import axios from "axios";
import Cookies from "js-cookie";

const Card = () => {
  const [cards, setCards] = useState([]);
  const [currentCardIndex, setCurrentCardIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    const token = Cookies.get('token');
    const headers = {
      Authorization: `Bearer ${token}`
    };

    axios.get('http://localhost:8080/customers/84/credit-cards', { headers })
      .then(response => {
        console.log(response.data)
        setCards(response.data);
      })
      .catch(error => {
        console.error('Произошла ошибка при получении данных', error);
      });
  }, []);

  const handlePrevCard = () => {
    if (!isAnimating && currentCardIndex > 0) {
      setIsAnimating(true);
      setTimeout(() => {
        setCurrentCardIndex(currentCardIndex - 1);
        setIsAnimating(false);
      }, 500); // Задержка в миллисекундах
    }
  };

  const handleNextCard = () => {
    if (!isAnimating && currentCardIndex < cards.length - 1) {
      setIsAnimating(true);
      setTimeout(() => {
        setCurrentCardIndex(currentCardIndex + 1);
        setIsAnimating(false);
      }, 500); // Задержка в миллисекундах
    }
  };

  const handleCreateCard = () => {
    // Реализуйте обработчик создания новой карты
    console.log("Создание новой карты...");
  };

  const formatCardNumber = (cardNumber) => {
    // ... То же, что и в предыдущем ответе ...
  };

  const currentCard = cards[currentCardIndex];

  return (
    <Wrap className={styles.cardWrapper}>
      <div className={styles.titleWrapper}>
        {/* ... */}
      </div>
      <div className={styles.container}>
        {currentCard && (
          <div className={styles.cardButtonsContainer}>
            <button className={`${styles.cardButtons} ${styles.leftButton}`} onClick={handlePrevCard}>
              <img src={left} alt="Previous" />
            </button>
            <div className={styles.card}>
              <div className={`${styles.front} ${styles.card_inner}`} style={{ transform: `rotateY(${isAnimating ? 180 : 0}deg)`, opacity: isAnimating ? 0 : 1 }}>
                {/* ... Остальные стили ... */}
              </div>
              <div className={`${styles.front} ${styles.card_inner}`} style={{ transform: `rotateY(${isAnimating ? 0 : 180}deg)`, opacity: isAnimating ? 1 : 0 }}>
                {/* ... Остальные стили ... */}
              </div>
            </div>
            {currentCardIndex === cards.length - 1 ? (
              <button className={`${styles.cardButtons} ${styles.rightButton}`} onClick={handleCreateCard}>
                <img src={right} alt="Create" />
              </button>
            ) : (
              <button className={`${styles.cardButtons} ${styles.createButton}`} onClick={handleNextCard}>
                <img src={visa} alt="Next" />
              </button>
            )}
          </div>
        )}
      </div>
    </Wrap>
  );
};

export default Card;
