import React, { useState } from "react";
import styles from "./Sidebar.module.css";
import Wrap from "../Wrap/Wrap";
import logoImage from "../images/logo/logo.jpg"; // Путь к изображению

const Sidebar = () => {
  const [activeButton, setActiveButton] = useState("Home");

  const handleButtonClick = (buttonText) => {
    setActiveButton(buttonText);
  };

  const buttonData = [
    { text: "Home", image: "path/to/home-image.png" },
    { text: "Transaction", image: "path/to/transaction-image.png" },
    { text: "Portfolio", image: "path/to/portfolio-image.png" },
    { text: "Wallet", image: "path/to/wallet-image.png" },
  ];

  return (
    <Wrap className={`${styles.sidebar}`}>
      <div className={`${styles.wrapLogo} ${styles.separator}`}>
        <img className={styles.logo} src={logoImage} alt="Logo" />
        <h1 className={styles.logoName}>Iron Bank</h1>
      </div>
      <div className={`${styles.navigation}`}>
        {buttonData.map((button) => (
          <div
            key={button.text}
            className={`${styles.wrapForElement} ${
              activeButton === button.text ? styles.clicked : ""
            }`}
          >
            <button
              className={`${styles.button}`}
              onClick={() => handleButtonClick(button.text)}
            >
              {button.text}
            </button>
          </div>
        ))}
      </div>
      <img src={buttonData.find((button) => button.text === activeButton).image} />
    </Wrap>
  );
};

export default Sidebar;
