import React, { useState } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';

const MyComponent = () => {
  const [error, setError] = useState(null);

  const handleLogin = async () => {
    try {
      const response = await axios.post("http://localhost:8080/auth/signin", {
        password: "string",
        username: "string",
      });

      const token = response.data.token;
      console.log('Токен:', token);

      // Сохраняем токен в куки на 1 день
      Cookies.set('token', token, { expires: 1 });

      setError(null);
    } catch (error) {
      setError(error);
    }
  };

  const handleGetData = async () => {
    try {
      // Получаем токен из кук
      const token = Cookies.get('token');

      if (!token) {
        console.log('Токен не найден в куках. Пользователь не авторизован.');
        return;
      }
      
      // Выполняем GET-запрос с заголовком Authorization
      const response = await axios.get("http://localhost:8080/exchange-rates/latest/pln", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      console.log('JSON-данные:', response.data);
      setError(null);
    } catch (error) {
      setError(error);
    }
  };

  return (
    <div>
      <button onClick={handleLogin}>Войти</button>
      <button onClick={handleGetData}>Получить данные</button>
      {error && <p>Произошла ошибка: {error.message}</p>}
    </div>
  );
};

export default MyComponent;
