import React, { useState, useEffect } from "react";
import styles from './Body.module.css';
import BodyWrap from '../Wrap/BodyWrap'
import TotalBalance from "../TotalBalance/TotalBalance";
import MarketButtons from "../MarketButtons/MarketButtons";
import Card from '../Card/Card';
import Currency from "../Currency/Currency";

const Body = () => {
    const [selectedCardId, setSelectedCardId] = useState(null);

    // Function to handle card selection
    const handleCardSelection = (cardId) => {
        setSelectedCardId(cardId);
    }

    // Use an effect to handle initial card selection when the component renders
    useEffect(() => {
        // Assuming you have access to the user's cards data here
        const userCards = []; // Replace this with the actual user's cards data
        if (userCards.length > 0) {
            // Set the ID of the first card initially
            setSelectedCardId(userCards[0].id);
        }
    }, []);

    return (
        <BodyWrap>
            <div className={styles.firstRow}>
                <TotalBalance cardId={selectedCardId} />
                <MarketButtons />
            </div>

            <div className={styles.secondRow}>
                <Card onCardSelect={handleCardSelection} />
                <Currency />
            </div>
        </BodyWrap>
    )
}

export default Body;
