import React, { useEffect, useState } from "react";
import styles from "./Currency.module.css";
import Wrap from "../Wrap/Wrap";
import ua from "../images/currencyFlags/ua.png";
import axios from "axios";
import Cookies from "js-cookie";

const Currency = () => {
  const [exchangeRates, setExchangeRates] = useState(null);

  useEffect(() => {
    const tokenFromCookie = Cookies.get("token");

    if (tokenFromCookie) {
      axios
        .get("http://localhost:8080/exchange-rates/latest/eur", {
          headers: {
            Authorization: `Bearer ${tokenFromCookie}`,
          },
        })
        .then((response) => {
          // Получаем объект с обменными курсами
          const conversionRates = response.data.conversion_rates;

          // Сортируем объект по обменным курсам
          const sortedRates = Object.entries(conversionRates).sort(
            ([, rate1], [, rate2]) => rate1 - rate2
          );

          // Создаем новый объект на основе отсортированных данных
          const sortedExchangeRates = Object.fromEntries(sortedRates);

          setExchangeRates(sortedExchangeRates);
        })
        .catch((error) => {
          console.error("Error fetching exchange rates:", error);
        });
    }
  }, []);

  return (
    <Wrap className={styles.currenceWrapper}>
      <h1>Currency</h1>
      <div className={styles.currencyList}>
        {exchangeRates ? (
          Object.entries(exchangeRates).map(([currency, rate]) => (
            <div key={currency} className={styles.currencyRow}>
              <div className={styles.currencyIconAndName}>
                {/* TODO: Добавьте иконку валюты */}
                <span className={styles.currencyName}>{currency}</span>
              </div>
              <div className={styles.currencyAmount}>
                <span>{rate}</span>
              </div>
            </div>
          ))
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </Wrap>
  );
};

export default Currency;
