import React, { Component } from "react";
import axios from "axios";
import Cookies from "js-cookie";

const MyComponent = () => {
  state = {
    error: null,
  };

  const handleLogin = async () => {
    try {
      const response = await axios.post("http://localhost:8080/auth/signin", {
        password: "string",
        username: "string",
      });

      const token = response.data.token;
      console.log("Токен:", token);

      // Сохраняем токен в куки на 1 день
      Cookies.set("token", token, { expires: 1 });

      this.setState({ error: null });
    } catch (error) {
      this.setState({ error });
    }
  };

  handleGetData = async () => {
    try {
      // Получаем токен из кук
      const token = Cookies.get("token");

      if (!token) {
        console.log("Токен не найден в куках. Пользователь не авторизован.");
        return;
      }

      // Выполняем GET-запрос с заголовком Authorization
      const response = await axios.get(
        "http://localhost:8080/exchange-rates/latest/pln",
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      console.log("JSON-данные:", response.data);
      this.setState({ error: null });
    } catch (error) {
      this.setState({ error });
    }
  };


    return (
      <div>
        <button onClick={this.handleLogin}>Войти</button>
        <button onClick={this.handleGetData}>Получить данные</button>
        {error && <p>Произошла ошибка: {error.message}</p>}
      </div>
    );
  
}

export default MyComponent;
