import React, { Component } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie'; // Импортируем библиотеку js-cookie

class MyComponent extends Component {
  state = {
    error: null,
  };

  handlePostData = async () => {
    try {
      const response = await axios.post("http://localhost:8080/auth/signin", {
        password: "string",
        username: "string",
      });

      const token = response.data.token;
      console.log('Токен:', token);

      // Сохраняем токен в куки на 1 день
      Cookies.set('token', token, { expires: 1 });

      this.setState({ error: null });
    } catch (error) {
      this.setState({ error });
    }
  };

  componentDidMount() {
    // Проверяем, есть ли сохраненный токен в куках при загрузке компонента
    const token = Cookies.get('token');
    if (token) {
      console.log('Токен из кук:', token);
    }
  }

  render() {
    const { error } = this.state;

    return (
      <div>
        <button onClick={this.handlePostData}>Отправить POST-запрос</button>
        {error && <p>Произошла ошибка: {error.message}</p>}
      </div>
    );
  }
}

export default MyComponent;
