import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const Login = () => {
  const history = useHistory();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // Проверка логина и пароля (в реальном приложении это будет обращение к серверу)
    const isValidUser = username === 'ваш_правильный_логин' && password === 'ваш_правильный_пароль';

    if (isValidUser) {
      // Перенаправление на основную страницу банка
      history.push('/bank-page');
    } else {
      alert('Неправильный логин или пароль');
    }
  };

  return (
    <div>
      <h2>Страница логина</h2>
      <div>
        <input
          type="text"
          placeholder="Логин"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </div>
      <div>
        <input
          type="password"
          placeholder="Пароль"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <button onClick={handleLogin}>Войти</button>
    </div>
  );
};

export default Login;
