import React, { useState } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';

const MyComponent = () => {
  const [error, setError] = useState(null);

  const handleLogin = async () => {
    try {
      const response = await axios.post("http://localhost:8080/auth/signin", {
        password: "string",
        username: "string",
      });

      const token = response.data.token;
      console.log('Токен:', token);

      // Сохраняем токен в куки на 1 день
      Cookies.set('token', token, { expires: 1 });

      setError(null);
    } catch (error) {
      setError(error);
    }
  };


  const handleGetData = async () => {
    try {
      const response = await axios.post("http://localhost:8080/auth/signin", {
        password: "string",
        username: "string",
      });

      const token = response.data.token;
      console.log('Токен:', token);

      // Сохраняем токен в куки на 1 день
      Cookies.set('token', token, { expires: 1 });

      setError(null);
    } catch (error) {
      setError(error);
    }
  };
  // const handleGetData = () => {
  //   const token = Cookies.get('token');
  
  //   if (!token) {
  //     console.log('Token not found in cookies. User not authorized.');
  //     return;
  //   }
  
  //   axios
  //     .get('http://localhost:8080/exchange-rates/latest/pln', {
  //       headers: {
  //         Authorization: `Bearer ${token}`,
  //       },
  //     })
  //     .then((response) => {
  //       console.log('JSON data:', response.data);
  //       setError(null); // Assuming setError is defined and used elsewhere in the code.
  //     })
  //     .catch((error) => {
  //       setError(error); // Assuming setError is defined and used elsewhere in the code.
  //     });
  // };
  

  return (
    <div>
      <button onClick={handleLogin}>Войти</button>
      <button onClick={handleGetData}>Получить данные</button>
      {error && <p>Произошла ошибка: {error.message}</p>}
    </div>
  );
};

export default MyComponent;
