import React, {useState} from "react";
import styles from "./Sidebar.module.css";
import Wrap from "../Wrap/Wrap";
import logoImage from "../images/logo/logo.jpg"; // Путь к изображению

const Sidebar = () => {
    const [isClicked, setIsClicked] = useState(false);

    const clickHandler = () =>{
        setIsClicked(!isClicked)
    }
  return ( 
    <Wrap className={`${styles.sidebar}`}>
      <div className={`${styles.wrapLogo} ${styles.separator}`}>
        <img className={styles.logo} src={logoImage} alt="Logo" />
        <h1 className={styles.logoName}>Iron Bank</h1>
      </div>
      <div className={`${styles.navigation}`}>
        <div className={`${styles.wrapForElement} ${isClicked ? styles.clicked : ""}`}>
          <button className={`${styles.button}` }onСlick={clickHandler}> Home</button>
        </div>
        <div className={styles.wrapForElement}>
          <button className={`${styles.button} ${styles.trans}`}> Transaction</button>
        </div>
        <div className={styles.wrapForElement}>
          <button className={`${styles.button} ${styles.port}`}> Portfolio</button>
        </div>
        <div className={styles.wrapForElement}>
          <button className={`${styles.button} ${styles.wallet}`}> Wallet</button>
        </div>
      </div>
    </Wrap>
  );
};

export default Sidebar;
