import React, { useState } from "react";
import styles from "./Sidebar.module.css";
import Wrap from "../Wrap/Wrap";
import logoImage from "../images/logo/logo.jpg"; // Путь к изображению

const Sidebar = () => {
  const [activeButtonIndex, setActiveButtonIndex] = useState(-1);
  const [isRed, setIsRed] = useState(false);

  const handleButtonClick = (index) => {
    if (activeButtonIndex === index) {
      return; // Ничего не делаем, если нажали на уже активную кнопку
    }
    setActiveButtonIndex(index);
    setIsRed(!isRed); // Меняем значение на противоположное при каждом клике
  };

  const buttons = [
    { text: "Home", style: "", icon: "home.png" },
    { text: "Transaction", style: styles.trans, icon: "transaction.png" },
    { text: "Portfolio", style: styles.port, icon: "portfolio.png" },
    { text: "Wallet", style: styles.wallet, icon: "wallet.png" },
  ];

  return (
    <Wrap className={`${styles.sidebar}`}>
      <div className={`${styles.wrapLogo} ${styles.separator}`}>
        <img className={styles.logo} src={logoImage} alt="Logo" />
        <h1 className={styles.logoName}>Iron Bank</h1>
      </div>
      <div className={`${styles.navigation}`}>
        {buttons.map((button, index) => (
          <div
            key={index}
            className={`${styles.wrapForElement} ${
              activeButtonIndex === index ? styles.clicked : ""
            }`}
          >
            <button
              className={`${styles.button} ${button.style}`}
              onClick={() => handleButtonClick(index)}
            >
              {button.text}
            </button>
            <span
              className={`${styles.buttonIcon}`}
              style={{
                backgroundImage: isRed
                  ? "url('../images/logo/logo.jpg')"
                  : `url('../images/sidebarIcons/${button.icon}')`,
              }}
            ></span>
          </div>
        ))}
      </div>
    </Wrap>
  );
};

export default Sidebar;
