import React, { useRef } from 'react';
import modalStyles from './Modal.module.css';

const Modal = ({ children, onClose, className }) => {
    const modalRef = useRef(null);

    const handleOutsideClick = (e) => {
        if (modalRef.current && !modalRef.current.contains(e.target)) {
            onClose();
        }
    };

    return (
<div className={`${modalStyles['modal-overlay']} ${className}`} onClick={handleOutsideClick}>
            <div className={`${modalStyles['dark-layer']}`} />
            <div className={`${modalStyles['modal-content']}`} ref={modalRef}>
                <span
                    className={`${modalStyles['close-button']} ${modalStyles['close-button-right']}`}
                    onClick={onClose}
                >
                    &#10006;
                </span>
                {children}
            </div>
        </div>
    );
};

export default Modal;
