import React, { useState } from "react";
import styles from './Body.module.css';
import BodyWrap from '../Wrap/BodyWrap'
import TotalBalance from "../TotalBalance/TotalBalance";
import MarketButtons from "../MarketButtons/MarketButtons";
import Card from '../Card/Card'
import Currency from "../Currency/Currency";

const Body = () => {
    const [selectedCardId, setSelectedCardId] = useState(null);

    // Функция для обновления выбранного айдишника карты
    const handleCardSelect = (cardId) => {
        setSelectedCardId(cardId);
    };

    return(
        <BodyWrap >
            <div className={styles.firstRow}>
                {/* Передаем выбранный айдишник в TotalBalance */}
                <TotalBalance selectedCardId={selectedCardId} />
                <MarketButtons/>
            </div>

            <div className={styles.secondRow}>
                {/* Передаем функцию обновления айдишника в Card */}
                <Card onCardSelect={handleCardSelect} />
                <Currency/>
            </div>
        
        </BodyWrap>
    )
}

export default Body;
