import React, { useState } from "react";
import styles from "./Header.module.css";

const Header = () => {
  const [isOpen, setIsOpen] = useState(true);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleArrowUpClick = (e) => {
    e.stopPropagation();
    setIsOpen(false);
  };
  
  const handleArrowDownClick = (e) => {
    e.stopPropagation();
    setIsOpen(true);
  };
  

  return (
    <div className={styles.header}>
      <h1 className={styles.headerTitle}>Wallet</h1>
      <div className={styles.headerWrapper}>
        <div className={styles.headerSearchWrapper}>
          <button className={styles.search}></button>
          <button className={styles.notification}></button>
        </div>
        <button className={styles.profileButton} onClick={toggleDropdown}>
          <span>lox </span>
          <span onClick={isOpen ? handleArrowUpClick : handleArrowDownClick}>
            {isOpen ? "▲" : "▼"}
          </span>
        </button>

        {isOpen && (
          <div className={`${styles.dropdown} ${isOpen ? "open" : ""}`}>
            {/* Здесь размещаем список с опциями */}
            <div className={styles.option}>Опция 1</div>
            <div className={styles.option}>Опция 2</div>
            <div className={styles.option}>Опция 3</div>
            {/* ... */}
          </div>
        )}
      </div>
    </div>
  );
};

export default Header;
