import React, {useState} from "react";
import styles from "./Sidebar.module.css";
import Wrap from "../Wrap/Wrap";
import logoImage from "../images/logo/logo.jpg"; // Путь к изображению

const Sidebar = () => {
    const [activeButton, setActiveButton] = useState(null);

    const handleButtonClick = (buttonName) => {
        setActiveButton(buttonName);
      };
  return ( 
    <Wrap className={`${styles.sidebar}`}>
      <div className={`${styles.wrapLogo} ${styles.separator}`}>
        <img className={styles.logo} src={logoImage} alt="Logo" />
        <h1 className={styles.logoName}>Iron Bank</h1>
      </div>
      <div className={`${styles.navigation}`}>
        <div className={`${styles.wrapForElement} ${isClicked ? styles.clicked : ""}`}>
        <button
        className={`${styles.button} ${activeButton === 'Home' ? styles.active : ''}`}
        onClick={() => handleButtonClick('Home')}
      >
        Home
      </button>        </div>
        <div className={`${styles.wrapForElement} ${isClicked ? styles.clicked : ""}`}>
        <button
        className={`${styles.button} ${activeButton === 'Wallet' ? styles.active : ''}`}
        onClick={() => handleButtonClick('Wallet')}
      >
        Wallet
      </button>        </div>
        <div className={`${styles.wrapForElement} ${isClicked ? styles.clicked : ""}`}>
        <button
        className={`${styles.button} ${activeButton === 'Transactions' ? styles.active : ''}`}
        onClick={() => handleButtonClick('Transactions')}
      >
        Transactions
      </button>        </div>
        <div className={`${styles.wrapForElement} ${isClicked ? styles.clicked : ""}`}>
          <button className={`${styles.button} ${styles.wallet}`}onClick={clickHandler}> Wallet</button>
        </div>
      </div>
    </Wrap>
  );
};

export default Sidebar;
