import React, { useEffect, useState } from "react";
import styles from "./Currency.module.css";
import Wrap from "../Wrap/Wrap";
import ua from "../images/currencyFlags/ua.png";
import axios from "axios";
import Cookies from "js-cookie";

const Currency = () => {
  const [exchangeRate, setExchangeRate] = useState(null);

  useEffect(() => {
    // Получаем токен из куки
    const tokenFromCookie = Cookies.get("token");

    if (tokenFromCookie) {
      // Выполняем GET-запрос с заголовком Bearer токена
      axios
        .get("http://localhost:8080/exchange-rates/latest/eur", {
          headers: {
            Authorization: `Bearer ${tokenFromCookie}`,
          },
        })
        .then((response) => {
          // Обработка данных
          setExchangeRate(response.data); // Предположим, что данные обменного курса приходят в response.data
          console.log(response.data.conversion_rates)
        })
        .catch((error) => {
          // Обработка ошибок
          console.error("Error fetching exchange rate:", error);
        });
    }
  }, []);

  return (
    <Wrap className={styles.currenceWrapper}>
      <h1>Currency</h1>
      <div className={styles.currencyRow}>
        <div className={styles.currencyIconAndName}>
          <img alt="Currency Icon" src={ua} />
          <span className={styles.currencyName}>ua</span>
        </div>
        <div className={styles.currencyAmount}>
          <span>{exchangeRate ? exchangeRate.rate : "Loading..."}</span>
        </div>
      </div>
    </Wrap>
  );
};

export default Currency;
