import React, { useState } from "react";
import Wrap from "../Wrap/Wrap";
import styles from './MarketButtons.module.css';
import deposit from '../images/MarketButtons/deposit.png';

const MarketButtons = () => {
    const marketButtons = [
        { img: deposit, text: "Deposit" },
        { img: deposit, text: "Send" },
        { img: deposit, text: "kakaya_to" },
        { img: deposit, text: "xuynya" },
        { img: deposit, text: "xuy" },
        { img: deposit, text: "ne_ebu" },
        { img: deposit, text: "lox" },
    ];

    const [isDepositPopupOpen, setIsDepositPopupOpen] = useState(false);
    const [selectedCard, setSelectedCard] = useState("");
    const [cardNumber, setCardNumber] = useState("");
    const [amount, setAmount] = useState("");

    const handleDepositClick = () => {
        setIsDepositPopupOpen(true);
    };

    const handleDepositSubmit = (e) => {
        e.preventDefault();
        // Здесь может быть обработка данных для депозита
        setIsDepositPopupOpen(false);
    };

    return (
        <Wrap className={styles.marketButtonsWrapper}>
            Markets
            <div className={styles.buttonsWrapper}>
                {marketButtons.map((button, index) => (
                    <button
                        key={index}
                        className={styles.wrapForMarketElement}
                        onClick={button.text === "Deposit" ? handleDepositClick : undefined}
                    >
                        <img src={button.img} alt='qwe'></img>
                        <span>{button.text}</span>
                    </button>
                ))}
            </div>
            {isDepositPopupOpen && (
                <div className={modalStyles['modal-overlay']}>
                <form onSubmit={handleDepositSubmit}>
                        <label>
                            Select Card:
                            <select
                                value={selectedCard}
                                onChange={(e) => setSelectedCard(e.target.value)}
                            >
                                <option value="card1">Card 1</option>
                                <option value="card2">Card 2</option>
                                {/* ... Добавьте другие опции для карт */}
                            </select>
                        </label>
                        <label>
                            Card Number:
                            <input
                                type="text"
                                value={cardNumber}
                                onChange={(e) => setCardNumber(e.target.value)}
                            />
                        </label>
                        <label>
                            Amount:
                            <input
                                type="number"
                                value={amount}
                                onChange={(e) => setAmount(e.target.value)}
                            />
                        </label>
                        <button type="submit">Submit</button>
                        <button onClick={() => setIsDepositPopupOpen(false)}>Close</button>
                    </form>
                </div>
            )}
        </Wrap>
    );
};

export default MarketButtons;
