import React, { useState, useRef, useEffect } from "react";
import styles from './Header.module.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [dropdownHeight, setDropdownHeight] = useState(0);
  const dropdownRef = useRef(null);

  useEffect(() => {
    setDropdownHeight(dropdownRef.current.clientHeight);
  }, [isMenuOpen]);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className={styles.header}>
      <h1 className={styles.headerTitle}>Wallet</h1>
      <div className={styles.headerWrapper}>
        <div className={styles.headerSearchWrapper}>
          <button className={styles.search}></button>
          <button className={styles.notification}></button>
        </div>
        <div
          className={`${styles.profileButtonWrapper} ${
            isMenuOpen ? styles.activeDropdown : ""
          }`}
        >
          <button className={styles.profileButton} onClick={toggleMenu}>
            <span>denis lox</span>
            <span>{isMenuOpen ? "▲" : "▼"}</span>
          </button>
          {isMenuOpen && (
            <div
              className={`${styles.dropdown}`}
              style={{ height: isMenuOpen ? dropdownHeight : 0 }}
              ref={dropdownRef}
            >
              <div className={styles.option}>Option 1</div>
              <div className={styles.option}>Option 2</div>
              <div className={styles.option}>Option 3</div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Header;
