import React, { useState, useRef } from 'react';
import styles from './RightSideLogin.module.css';
import axios from 'axios';
import Cookies from 'js-cookie';
import { useNavigate } from 'react-router-dom';

const RightSideLogin = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const inputRef1 = useRef(null);
  const inputRef2 = useRef(null);

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:8080/auth/signin', {
        username,
        password,
      });

      const token = response.data.token;
      console.log('Токен:', token);

      // Save the token in cookies for 1 day
      Cookies.set('token', token, { expires: 1 });

      setError(null);

      // Redirect to the main page
      navigate('/bank-page');
    } catch (error) {
      setError(error);
    }
  };
  

  const handleSignUp = () => {
    // Redirect to the signup page
    navigate('/sign-up');
  };


  const handleInputClick = (inputRef) => {
    // Добавляем класс 'clicked' при клике на инпут
    inputRef.current.classList.add('clicked');

    // Удаляем класс 'clicked' через некоторое время (можно настроить)
    setTimeout(() => {
      inputRef.current.classList.remove('clicked');
    }, 1000); // Удалить класс через 1 секунду (настройте время по вашему усмотрению)
  };
  return (
    <div className={styles.rightSide}>
      <div className={styles.rightSideLoginWrapper}>
        <h1 className={styles.title}>Sign in with password</h1>
        <div>
        <input
            placeholder='Username'
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            ref={inputRef1}
            onClick={() => handleInputClick(inputRef1)}
          />
          <input
            placeholder='Password'
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            ref={inputRef2}
            onClick={() => handleInputClick(inputRef2)}
          /> </div>
        <button onClick={handleLogin}>Log in</button>
        {/* <button onClick={handleSignUp}>Sign up</button> */}
        {error && <p>Произошла ошибка: {error.message}</p>}
      </div>
    </div>
  );
};

export default RightSideLogin;
