import React, { useState, useEffect } from "react";
import styles from "./TotalBalance.module.css";
import Wrap from "../Wrap/Wrap";

const TotalBalance = () => {
  // Sample data for income and expenses
  const [income, setIncome] = useState(30);
  const [expenses, setExpenses] = useState(40);
  const [percentage, setPercentage] = useState(0);
  const [isPositive, setIsPositive] = useState(false);

  useEffect(() => {
    // Calculate the percentage
    const calculatedPercentage =
      expenses === 0
        ? income * 100
        : (((income - expenses) / expenses) * 100).toFixed(2);
    setPercentage(calculatedPercentage);
    setIsPositive(calculatedPercentage >= 0);
  }, [income, expenses]);

  return (
    <Wrap className={styles.wrapperTotalBAlance}>
      {/* Your other content */}
      <h1 className={styles.amountTitle}>Total balance</h1>
      <div className={styles.amountWrapper}>
        <span className={styles.amount}>
          $120<span>.20</span>
        </span>
        <div
          className={styles.amountPercentageWrapper}
          style={{ backgroundColor: isPositive ? "#07f8b5" : "#ff3333" }}
        >
          <span className={styles.amountPercentage}>
          {isPositive ? (
              <span className={`${styles.triangle} ${styles.upTriangle}`}>&#9650;</span>
            ) : (
              <span className={`${styles.triangle} ${styles.downTriangle}`}>&#9660;</span>
            )}            {Math.abs(percentage)}%
          </span>
        </div>
      </div>
      <div className={styles.spendingsWrapper}>
        <span className={styles.income}>${income}</span>
        <span className={styles.expenses}>${expenses}</span>
      </div>
    </Wrap>
  );
};

export default TotalBalance;
