import React, { useState } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import { useHistory } from 'react-router-dom';


const Login = () => {
  const history = useHistory();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:8080/auth/signin', {
        username,
        password,
      });

      const token = response.data.token;
      console.log('Токен:', token);

      // Save the token in cookies for 1 day
      Cookies.set('token', token, { expires: 1 });

      setError(null);

      // Redirect to the main page
      history.push('/bank-page');
    } catch (error) {
      setError(error);
    }
  };

  const handleGetData = async () => {
    const token = Cookies.get('token');

    if (!token) {
      console.log('Token not found in cookies. User not authorized.');
      return;
    }

    try {
      const response = await axios.get('http://localhost:8080/exchange-rates/latest/pln', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      console.log(response.data);

      setError(null);
    } catch (error) {
      console.error(error);
      setError(error);
    }
  };

  return (
    <div>
      <div>
        <label>
          Username:
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
        </label>
      </div>
      <div>
        <label>
          Password:
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </label>
      </div>
      <button onClick={handleLogin}>Войти</button>
      <button onClick={handleGetData}>Получить данные</button>
      {error && <p>Произошла ошибка: {error.message}</p>}
    </div>
  );
};

export default Login;
