import React, { useState } from "react";
import Wrap from "../Wrap/Wrap";
import styles from './MarketButtons.module.css';
import deposit from '../images/MarketButtons/deposit.png';

const MarketButtons = () => {
    const marketButtons = [
        { img: deposit, text: "Deposit" },
        { img: deposit, text: "Send" },
        { img: deposit, text: "kakaya_to" },
        { img: deposit, text: "xuynya" },
        { img: deposit, text: "xuy" },
        { img: deposit, text: "ne_ebu" },
        { img: deposit, text: "lox" },
    ];

    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const [selectedCard, setSelectedCard] = useState(""); // Выбранная карта
    const [cardNumber, setCardNumber] = useState(""); // Номер карты
    const [amount, setAmount] = useState(""); // Сумма

    // Функция для обработки отправки формы
    const handleSubmit = (e) => {
        e.preventDefault();
        // Здесь вы можете выполнить логику для обработки данных
        // Например, отправка данных на сервер
        // Закрыть окно после успешной обработки
        setIsPopupOpen(false);
    };

    return (
        <Wrap className={styles.marketButtonsWrapper}>
            Markets
            <div className={styles.buttonsWrapper}>
                {marketButtons.map((button, index) => (
                    <button
                        key={index}
                        className={styles.wrapForMarketElement}
                        onClick={() => setIsPopupOpen(true)}
                    >
                        <img src={button.img} alt='qwe'></img>
                        <span>{button.text}</span>
                    </button>
                ))}
            </div>
            {isPopupOpen && (
                <div className={styles.popup}>
                    <form onSubmit={handleSubmit}>
                        <label>
                            Select Card:
                            <select
                                value={selectedCard}
                                onChange={(e) => setSelectedCard(e.target.value)}
                            >
                                {/* Здесь вы можете отобразить все карты пользователя */}
                                <option value="card1">Card 1</option>
                                <option value="card2">Card 2</option>
                                {/* ... */}
                            </select>
                        </label>
                        <label>
                            Card Number:
                            <input
                                type="text"
                                value={cardNumber}
                                onChange={(e) => setCardNumber(e.target.value)}
                            />
                        </label>
                        <label>
                            Amount:
                            <input
                                type="number"
                                value={amount}
                                onChange={(e) => setAmount(e.target.value)}
                            />
                        </label>
                        <button type="submit">Submit</button>
                        <button onClick={() => setIsPopupOpen(false)}>Close</button>
                    </form>
                </div>
            )}
        </Wrap>
    );
};

export default MarketButtons;
