import React, {useState} from "react";
import styles from "./Card.module.css";
import Wrap from "../Wrap/Wrap";
import settingsSign from "../images/other/settings.png";

const Body = () => {


    const [isFlipped, setIsFlipped] = useState(false);

    const handleClick = () => {
      setIsFlipped((prevFlipped) => !prevFlipped);
    };

  return (
    <Wrap className={styles.cardWrapper}>
      <div className={styles.titleWrapper}>
        <h1>Card Lists</h1>
        <button className={styles.settingSignButton}>
          <img
            className={styles.settingSign}
            src={settingsSign}
            alt="Settings Card"
          />
        </button>
      </div>

      <div className={styles.cardSlider}>
        <div
          className={`${styles.card} ${isFlipped ? styles.flipped : ""}`}
          onClick={handleClick}
        >
          <div className={styles.cardFront}>
        <div className={styles.cardLogo}>Logo</div>
        <div className={styles.cardNumber}>9759 2484 5269 6576</div>
        <div className={styles.cardInfo}>
          <div className={styles.cardHolder}>
            <div className={styles.cardHolderLabel}>Card Holder</div>
            <div className={styles.cardHolderName}>Bruce Wayne</div>
          </div>
          <div className={styles.cardExpiration}>
            <div className={styles.cardExpirationLabel}>Expires</div>
            <div className={styles.cardExpirationDate}>12/24</div>
          </div>
        </div>
      </div>
      <div className={styles.cardBack}>
        <div className={styles.cardBackMagneticStripe}></div>
        <div className={styles.cardBackSignatureStrip}></div>
        <div className={styles.cardBackCVV}>
          <div className={styles.cardBackCVVLabel}>CVV</div>
          <div className={styles.cardBackCVVNumber}>123</div>
        </div>
      </div>
    </div>




    </div>

    </Wrap>
  );
};

export default Body;
