import React, { useState, useEffect } from "react";
import styles from "./Card.module.css";
import Wrap from "../Wrap/Wrap";
import settingsSign from "../images/other/settings.png";
import map from '../images/card/map.png'
import chip from '../images/card/chip.png'
import visa from '../images/card/visa.png'
import axios from "axios";
import Cookies from "js-cookie";

const Card = () => {
  const [cards, setCards] = useState([]);
  const [currentCardIndex, setCurrentCardIndex] = useState(0);
  const [isCreatingCard, setIsCreatingCard] = useState(false);

  useEffect(() => {
    const token = Cookies.get('token');
    const headers = {
      Authorization: `Bearer ${token}`
    };

    axios.get('http://localhost:8080/customers/84/credit-cards', { headers })
      .then(response => {
        setCards(response.data);
      })
      .catch(error => {
        console.error('Произошла ошибка при получении данных', error);
      });
  }, []);

  const handlePrevCard = () => {
    if (isCreatingCard && currentCardIndex === 0) {
      setIsCreatingCard(false);
    } else {
      setCurrentCardIndex((prevIndex) => (prevIndex - 1 + cards.length) % cards.length);
    }
  };

  const handleNextCard = () => {
    if (currentCardIndex === cards.length - 1) {
      setIsCreatingCard(true);
    } else {
      setCurrentCardIndex((prevIndex) => (prevIndex + 1) % cards.length);
    }
  };

  const currentCard = cards[currentCardIndex];

  return (
    <Wrap className={styles.cardWrapper}>
      <div className={styles.titleWrapper}>
        <h1>Card Lists</h1>
        <button className={styles.settingSignButton}>
          <img
            className={styles.settingSign}
            src={settingsSign}
            alt="Settings Card"
          />
        </button>
      </div>
      <div className={styles.container}>
        {cards.length > 0 && (
          <div className={styles.card}>
            <div className={styles.card_inner}>
              <div className={styles.front}>
                <img src={map} className={styles.map_img} alt="map" />
                <div className={styles.row}>
                  <img src={chip} width="40px" alt="chip" />
                  <img src={visa} width="40px" alt="visa" />
                </div>
                <div className={`${styles.row} ${styles.card_no}`}>
                  <p>{currentCard.cardNumber}</p>
                </div>
                <div className={`${styles.row} ${styles.card_holder}`}>
                  <p>CARD HOLDER: {currentCard.cardHolder}</p>
                  <p>VALID TILL: {currentCard.expirationDate}</p>
                </div>
                <div className={`${styles.row} ${styles.name}`}>
                  <p>{currentCard.name}</p>
                </div>
              </div>
            </div>
          </div>
        )}
        <div>
          {isCreatingCard ? (
            <div>
              <h2>Create a New Card</h2>
              {/* Форма для создания новой карты */}
            </div>
          ) : (
            <div>
              <h2>Card Details</h2>
              {cards.length > 0 && (
                <div>
                  <button onClick={handlePrevCard}>Previous Card</button>
                  <button onClick={handleNextCard}>
                    {currentCardIndex === cards.length - 1 ? "Create New Card" : "Next Card"}
                  </button>
                </div>
              )}
              {currentCard && (
                <div>
                  <p>Card Number: {currentCard.cardNumber}</p>
                  <p>CVV: {currentCard.cvv}</p>
                  <p>Billing Address: {currentCard.billingAddress}</p>
                  {/* Дополнительные поля карты */}
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </Wrap>
  );
};

export default Card;
