import React, { useState } from "react";
import styles from './newCard.module.css'

const NewCard = (props) => {
  const [inputValue, setInputValue] = useState(""); // Состояние для хранения значения ввода
  const [error, setError] = useState(""); // Состояние для хранения ошибки

  // Функция для проверки валидности формы
  const isFormValid = (value) => {
    // Проверка на наличие текста
    if (value.trim() === "") {
      setError("Please enter card holder's name");
      return false;
    }
    
    // Проверка на маленькие буквы и пробелы
    const regex = /^[a-z\s]+$/;
    if (!regex.test(value)) {
      setError("Only lowercase letters and spaces are allowed");
      return false;
    }
    
    setError(""); // Очистить ошибку, если ввод валиден
    return true;
  };

  // Обработчик изменения значения ввода
  const handleInputChange = (e) => {
    const value = e.target.value;
    setInputValue(value);
    isFormValid(value); // Проверить валидность при изменении значения ввода
  };

  return (
    <div className={styles.wrapper} >
      <h1>Add new card</h1>
      <div className={styles.addingNewCard}>
        <div className={styles.inputInf}>
          <span>Enter please card's holder</span>
          <input
            className={styles.inputForCardNumber}
            type="text"
            placeholder="Nick Johnson"
            value={inputValue}
            onChange={handleInputChange} // Используйте обработчик onChange
          />
        </div>

        {/* Отображение ошибки */}
        {error && <div className={styles.error}>{error}</div>}

        <button
          className={`${styles.formButton} ${isFormValid(inputValue) ? styles.formButtonActive : ''}`}
          type="submit"
          onClick={() => props.onConfirm(props)}
          disabled={!isFormValid(inputValue)} // Деактивируйте кнопку, если форма недействительна
        >
          Submit
        </button>
      </div>
    </div>
  );
};

export default NewCard;
