import React, { useEffect, useState } from "react";
import styles from './PiggyBank.module.css'
import plus from '../images/other/plus.png'
import Cookies from "js-cookie";
import axios from "axios";
const PiggyBank = () => {
    const [collected, setCollected] = useState(200); // Поменял значение для демонстрации переполнения
    const [goal, setGoal] = useState(200);
    const [customerId, setCustomerId] = useState(null)
    const [accountID, setAccountID] = useState(null)
    const [piggyBanks, setPiggyBanks] = useState([null])
    const progress = (collected / goal) * 100;
    const isFull = progress >= 100;


    useEffect(() => {
        const token = Cookies.get('token')
        const username = Cookies.get('username')
        const headers = {
            Authorization: `Bearer ${token}`
        };
        axios.get('http://localhost:8080/customers', { headers }).then((res) => {
            const matchingUser = res.data.find(arr => arr.username === username)
            if (matchingUser) {
                setCustomerId(matchingUser.id)
            }
        }).catch(error => {
            console.error('Error fetching customer data', error);
        });
        axios.get('http://localhost:8080/accounts', { headers }).then((res) => {
            const matchingAccount = res.data.find(arr => arr.customerId === customerId)
            if (matchingAccount) {
                setAccountID(matchingAccount.id)

            }
        }).catch(error => {
            console.error('Error fetching customer data', error);
        });
        axios.get('http://localhost:8080/piggy-banks', { headers }).then((res) => {
            const piggyBanksData = res.data;

            // Фильтруем копилки по accountID
            const filteredPiggyBanks = piggyBanksData.filter(piggyBank => piggyBank.accountID === accountID);

            setPiggyBanks(filteredPiggyBanks);
            
        }).catch(error => {
            console.error('Error fetching piggy banks data', error);
        });
    }, [customerId, accountID, piggyBanks]);
    return (
        <div className={styles.piggyBank}>
            <h1>Piggy Bank</h1>
            <div className={styles.bankWrapper}>
                <div className={styles.jar}>
                    <div className={styles.topOfJar}></div>
                    <div className={styles.bottomOfJar}></div>
                </div>
                <div className={styles.bankDescription}>
                    <h2 className={styles.boxName}>Name of the box</h2>
                    <span className={styles.collected}> {collected} euro collected from {goal}</span>
                    <div className={`${styles.progressionBar} `}>
                        <div className={`${styles.progressBarFill} ${isFull ? styles.full : ''}`} style={{ width: `${progress}%` }}></div>
                    </div>
                </div>
            </div>
            <div className={styles.addNewPiggy}>
                <div>
                    <img src={plus} alt="plus" />
                    <span> Add new box</span>
                </div>
            </div>
        </div>
    )
}

export default PiggyBank
