import React, { useState } from "react";
import styles from './Header.module.css'
import axios from "axios";
import Cookies from "js-cookie";

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);


  useEffect(() => {
    const tokenFromCookie = Cookies.get("token");

    if (tokenFromCookie) {
      axios
        .get("http://localhost:8080/exchange-rates/latest/eur", {
          headers: {
            Authorization: `Bearer ${tokenFromCookie}`,
          },
        })
        .then((response) => {
          // Получаем объект с обменными курсами
          const conversionRates = response.data.conversion_rates;
          setExchangeRates(conversionRates);
        })
        .catch((error) => {
          console.error("Error fetching exchange rates:", error);
        });
    }
  }, []);


  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className={styles.header}>
      <h1 className={styles.headerTitle}>Wallet</h1>
      <div className={styles.headerWrapper}>
        <div className={styles.headerSearchWrapper}>
          <button className={styles.search}></button>
          <button className={styles.notification}></button>
        </div>
        <div
          className={`${styles.profileButtonWrapper} ${isMenuOpen ? styles.activeDropdown : ""
            }`}
        >
          <button className={styles.profileButton} onClick={toggleMenu}>
            <span>not&gt;Than10</span>
            <span>{isMenuOpen ? "▲" : "▼"}</span>
          </button>
          {isMenuOpen && (
            <div className={`${styles.dropdown}`}>
              <div className={styles.option}>Denis</div>
              <div className={styles.option}>Polnyi </div>
              <div className={styles.option}>Lox</div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Header;
