import React, { useState } from 'react';
import styles from './RightSideLogin.module.css';
import axios from 'axios';
import Cookies from 'js-cookie';
import { useNavigate } from 'react-router-dom';

const RightSideLogin = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:8080/auth/signin', {
        username,
        password,
      });

      const token = response.data.token;
      console.log('Токен:', token);

      // Save the token in cookies for 1 day
      Cookies.set('token', token, { expires: 1 });

      setError(null);

      // Redirect to the main page
      navigate('/bank-page');
    } catch (error) {
      setError(error);
    }
  };

  const isButtonClickable = username.trim() !== '' && password.trim() !== '';

  // Изменяем цвет кнопки в зависимости от кликабельности
  const buttonStyle = {
    backgroundColor: isButtonClickable ? '#0056B3' : '#767676',
    color: isButtonClickable ? '#fff' : 'var(--text-color)',
    cursor: isButtonClickable ? 'pointer' : 'default',
  };
  const handleSignUp = () => {
    // Redirect to the signup page
    navigate('/sign-up');
  };
  return (
    <div className={styles.rightSide}>
      <div className={styles.rightSideLoginWrapper}>
        <h1 className={styles.title}>Sign in with password</h1>
        <div>
          <input placeholder='Username' type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
          <input placeholder='Password' type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <button className={styles.formButton} onClick={handleLogin}>Log in</button>
        {/* <button onClick={handleSignUp}>Sign up</button> */}
        {error && <p>Произошла ошибка: {error.message}</p>}
      </div>
    </div>
  );
};

export default RightSideLogin;
