import React, { useState } from "react";
import Wrap from "../Wrap/Wrap";
import styles from './MarketButtons.module.css';
import deposit from '../images/MarketButtons/deposit.png';
import modalStyles from './Modal.module.css'; // Импортируем стили для модального окна

const MarketButtons = () => {
    const marketButtons = [
        { img: deposit, text: "Deposit" },
        { img: deposit, text: "Send" },
        { img: deposit, text: "kakaya_to" },
        { img: deposit, text: "xuynya" },
        { img: deposit, text: "xuy" },
        { img: deposit, text: "ne_ebu" },
        { img: deposit, text: "lox" },
    ];

    const [isDepositPopupOpen, setIsDepositPopupOpen] = useState(false);
    const [selectedCard, setSelectedCard] = useState("");
    const [cardNumber, setCardNumber] = useState("");
    const [amount, setAmount] = useState("");

    const handleDepositClick = () => {
        setIsDepositPopupOpen(true);
    };

    const handleDepositSubmit = (e) => {
        e.preventDefault();
        // Здесь может быть обработка данных для депозита
        setIsDepositPopupOpen(false);
    };

    return (
        <Wrap className={styles.marketButtonsWrapper}>
            Markets
            <div className={styles.buttonsWrapper}>
                {marketButtons.map((button, index) => (
                    <button
                        key={index}
                        className={styles.wrapForMarketElement}
                        onClick={button.text === "Deposit" ? handleDepositClick : undefined}
                    >
                        <img src={button.img} alt='qwe'></img>
                        <span>{button.text}</span>
                    </button>
                ))}
            </div>
            {isModalOpen && (
                <Modal onClose={handleModalToggle}>
                    <form>
                        {/* Ваша форма */}
                        <label>
                            Select Card:
                            <select>
                                {/* Ваш выпадающий список */}
                            </select>
                        </label>
                        <label>
                            Card Number:
                            <input type="text" />
                        </label>
                        <label>
                            Amount:
                            <input type="number" />
                        </label>
                        <button type="submit">Submit</button>
                    </form>
                </Modal>
            )}
        </Wrap>
    );
};

export default MarketButtons;
